    private void permissionsComboBoxItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_permissionsComboBoxItemStateChanged
        if(permissionsComboBox.getSelectedItem() == "Ticketer"){
            officerNumTextField.setEnabled(true);
        }
        else {
            officerNumTextField.setEnabled(false);
        }
    }//GEN-LAST:event_permissionsComboBoxItemStateChanged

